import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const Dashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const response = await axios.get(
        'http://localhost:5001/api/challans/stats',
        {
          headers: { Authorization: `Bearer ${user.token}` }
        }
      );
      setStats(response.data);
    } catch (error) {
      console.error('Failed to fetch stats:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-gray-500">Loading dashboard...</div>
      </div>
    );
  }

  return (
    <div>
      <h1 className="text-3xl font-bold text-gray-900 mb-8">
        Welcome, {user.name}!
      </h1>

      {user.role === 'citizen' ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h3 className="text-lg font-semibold text-gray-900 mb-2">
              Total Challans
            </h3>
            <p className="text-3xl font-bold text-blue-600">
              {stats.totalChallans || 0}
            </p>
          </div>

          <div className="bg-white p-6 rounded-lg shadow-md">
            <h3 className="text-lg font-semibold text-gray-900 mb-2">
              Pending
            </h3>
            <p className="text-3xl font-bold text-yellow-600">
              {stats.pendingChallans || 0}
            </p>
          </div>

          <div className="bg-white p-6 rounded-lg shadow-md">
            <h3 className="text-lg font-semibold text-gray-900 mb-2">
              Paid
            </h3>
            <p className="text-3xl font-bold text-green-600">
              {stats.paidChallans || 0}
            </p>
          </div>

          <div className="bg-white p-6 rounded-lg shadow-md">
            <h3 className="text-lg font-semibold text-gray-900 mb-2">
              Total Fines
            </h3>
            <p className="text-3xl font-bold text-red-600">
              ₹{stats.totalFineAmount || 0}
            </p>
          </div>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h3 className="text-lg font-semibold text-gray-900 mb-2">
              Challans Issued
            </h3>
            <p className="text-3xl font-bold text-blue-600">
              {stats.issuedChallans || 0}
            </p>
          </div>

          <div className="bg-white p-6 rounded-lg shadow-md">
            <h3 className="text-lg font-semibold text-gray-900 mb-2">
              Today's Challans
            </h3>
            <p className="text-3xl font-bold text-green-600">
              {stats.todayChallans || 0}
            </p>
          </div>
        </div>
      )}

      <div className="mt-8 bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-semibold text-gray-900 mb-4">
          Quick Actions
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {user.role === 'citizen' ? (
            <>
              <a
                href="/challans"
                className="block p-4 bg-blue-50 rounded-lg hover:bg-blue-100 transition"
              >
                <h3 className="font-semibold text-blue-900">View My Challans</h3>
                <p className="text-sm text-blue-700">Check your traffic violations</p>
              </a>
              <a
                href="/payments"
                className="block p-4 bg-green-50 rounded-lg hover:bg-green-100 transition"
              >
                <h3 className="font-semibold text-green-900">Payment History</h3>
                <p className="text-sm text-green-700">View payment records</p>
              </a>
              <a
                href="/profile"
                className="block p-4 bg-purple-50 rounded-lg hover:bg-purple-100 transition"
              >
                <h3 className="font-semibold text-purple-900">Update Profile</h3>
                <p className="text-sm text-purple-700">Manage your information</p>
              </a>
            </>
          ) : (
            <>
              <a
                href="/challans"
                className="block p-4 bg-blue-50 rounded-lg hover:bg-blue-100 transition"
              >
                <h3 className="font-semibold text-blue-900">Issue Challan</h3>
                <p className="text-sm text-blue-700">Create new traffic violation</p>
              </a>
              <a
                href="/challans"
                className="block p-4 bg-green-50 rounded-lg hover:bg-green-100 transition"
              >
                <h3 className="font-semibold text-green-900">Manage Challans</h3>
                <p className="text-sm text-green-700">View issued challans</p>
              </a>
              <a
                href="/profile"
                className="block p-4 bg-purple-50 rounded-lg hover:bg-purple-100 transition"
              >
                <h3 className="font-semibold text-purple-900">Update Profile</h3>
                <p className="text-sm text-purple-700">Manage your information</p>
              </a>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
