import React, { createContext, useState, useContext, useEffect } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(false);
    const [newVehicle, setNewVehicle] = useState('');

    useEffect(() => {
        fetchProfile();
    }, []);

    const fetchProfile = async () => {
        try {
            const response = await axios.get(
                'http://localhost:5001/api/auth/profile',
                {
                    headers: { Authorization: `Bearer ${user.token}` }
                }
            );
            setFormData({
                name: response.data.name || '',
                email: response.data.email || '',
                phone: response.data.phone || '',
                address: response.data.address || '',
                licenseNumber: response.data.licenseNumber || '',
                vehicleNumbers: response.data.vehicleNumbers || []
            });
        } catch (error) {
            console.error('Failed to fetch profile:', error);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            await axios.put(
                'http://localhost:5001/api/auth/profile',
                formData,
                {
                    headers: { Authorization: `Bearer ${user.token}` }
                }
            );
            alert('Profile updated successfully!');
        } catch (error) {
            alert(error.response?.data?.message || 'Failed to update profile');
        } finally {
            setLoading(false);
        }
    };

    const addVehicle = () => {
        if (newVehicle.trim() && !formData.vehicleNumbers.includes(newVehicle.trim())) {
            setFormData({
                ...formData,
                vehicleNumbers: [...formData.vehicleNumbers, newVehicle.trim()]
            });
            setNewVehicle('');
        }
    };

    const removeVehicle = (vehicleToRemove) => {
        setFormData({
            ...formData,
            vehicleNumbers: formData.vehicleNumbers.filter(vehicle => vehicle !== vehicleToRemove)
        });
    };

    return (
        <div className="max-w-2xl mx-auto">
            <h1 className="text-3xl font-bold text-gray-900 mb-8">Profile Settings</h1>

            <div className="bg-white p-8 rounded-lg shadow-md">
                <form onSubmit={handleSubmit} className="space-y-6">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                Full Name
                            </label>
                            <input
                                type="text"
                                required
                                value={formData.name}
                                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                                className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                            />
                        </div>

                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                Email Address
                            </label>
                            <input
                                type="email"
                                value={formData.email}
                                disabled
                                className="w-full p-3 border border-gray-300 rounded-md bg-gray-100 text-gray-500"
                            />
                            <p className="text-xs text-gray-500 mt-1">Email cannot be changed</p>
                        </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                Phone Number
                            </label>
                            <input
                                type="tel"
                                value={formData.phone}
                                onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                                className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                            />
                        </div>

                        {user.role === 'citizen' && (
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                    License Number
                                </label>
                                <input
                                    type="text"
                                    value={formData.licenseNumber}
                                    onChange={(e) => setFormData({ ...formData, licenseNumber: e.target.value })}
                                    className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                                />
                            </div>
                        )}
                    </div>

                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">
                            Address
                        </label>
                        <textarea
                            value={formData.address}
                            onChange={(e) => setFormData({ ...formData, address: e.target.value })}
                            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                            rows="3"
                        />
                    </div>

                    {user.role === 'citizen' && (
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                Vehicle Numbers
                            </label>
                            <div className="space-y-3">
                                <div className="flex space-x-2">
                                    <input
                                        type="text"
                                        value={newVehicle}
                                        onChange={(e) => setNewVehicle(e.target.value)}
                                        className="flex-1 p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                                        placeholder="Enter vehicle number (e.g., ABC-1234)"
                                    />
                                    <button
                                        type="button"
                                        onClick={addVehicle}
                                        className="bg-green-600 text-white px-4 py-3 rounded-md hover:bg-green-700"
                                    >
                                        Add
                                    </button>
                                </div>

                                {formData.vehicleNumbers.length > 0 && (
                                    <div className="space-y-2">
                                        {formData.vehicleNumbers.map((vehicle, index) => (
                                            <div key={index} className="flex items-center justify-between bg-gray-50 p-2 rounded">
                                                <span>{vehicle}</span>
                                                <button
                                                    type="button"
                                                    onClick={() => removeVehicle(vehicle)}
                                                    className="text-red-600 hover:text-red-800"
                                                >
                                                    Remove
                                                </button>
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </div>
                        </div>
                    )}

                    <div className="pt-4">
                        <button
                            type="submit"
                            disabled={loading}
                            className="w-full bg-blue-600 text-white py-3 px-4 rounded-md hover:bg-blue-700 disabled:opacity-50"
                        >
                            {loading ? 'Updating...' : 'Update Profile'}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default Profile;
