import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const ChallanList = ({ challans, onChallanUpdate }) => {
    const { user } = useAuth();
    const [payingChallan, setPayingChallan] = useState(null);
    const [paymentMethod, setPaymentMethod] = useState('Card');

    const getStatusBadge = (status) => {
        const colors = {
            pending: 'bg-yellow-100 text-yellow-800',
            paid: 'bg-green-100 text-green-800',
            disputed: 'bg-red-100 text-red-800',
            cancelled: 'bg-gray-100 text-gray-800'
        };

        return (
            <span className={`px-2 py-1 rounded-full text-xs font-medium ${colors[status]}`}>
                {status.charAt(0).toUpperCase() + status.slice(1)}
            </span>
        );
    };

    const handlePayment = async (challanId) => {
        try {
            const response = await axios.post(
                'http://localhost:5001/api/payments/process',
                { challanId, paymentMethod },
                {
                    headers: { Authorization: `Bearer ${user.token}` }
                }
            );

            alert('Payment successful!');
            onChallanUpdate(response.data.challan);
            setPayingChallan(null);
        } catch (error) {
            alert(error.response?.data?.message || 'Payment failed');
        }
    };

    const formatDate = (date) => {
        return new Date(date).toLocaleDateString('en-IN');
    };

    if (challans.length === 0) {
        return (
            <div className="text-center py-8">
                <p className="text-gray-500">No challans found.</p>
            </div>
        );
    }

    return (
        <div className="space-y-4">
            {challans.map((challan) => (
                <div key={challan._id} className="bg-white p-6 rounded-lg shadow-md">
                    <div className="flex justify-between items-start mb-4">
                        <div>
                            <h3 className="text-lg font-semibold text-gray-900">
                                {challan.challanNumber}
                            </h3>
                            <p className="text-sm text-gray-600">
                                {challan.violationType} • {challan.vehicleNumber}
                            </p>
                        </div>
                        {getStatusBadge(challan.status)}
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                        <div>
                            <p className="text-sm text-gray-600">Location</p>
                            <p className="font-medium">{challan.location}</p>
                        </div>
                        <div>
                            <p className="text-sm text-gray-600">Fine Amount</p>
                            <p className="font-medium text-red-600">₹{challan.fineAmount}</p>
                        </div>
                        <div>
                            <p className="text-sm text-gray-600">Date</p>
                            <p className="font-medium">{formatDate(challan.dateTime)}</p>
                        </div>
                        <div>
                            <p className="text-sm text-gray-600">Due Date</p>
                            <p className="font-medium">{formatDate(challan.dueDate)}</p>
                        </div>
                    </div>

                    {challan.description && (
                        <div className="mb-4">
                            <p className="text-sm text-gray-600">Description</p>
                            <p className="text-sm">{challan.description}</p>
                        </div>
                    )}

                    {user.role === 'citizen' && challan.status === 'pending' && (
                        <div className="flex space-x-2">
                            <button
                                onClick={() => setPayingChallan(challan._id)}
                                className="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700"
                            >
                                Pay Now
                            </button>
                        </div>
                    )}

                    {challan.status === 'paid' && challan.paymentDate && (
                        <div className="mt-4 p-3 bg-green-50 rounded-md">
                            <p className="text-sm text-green-800">
                                ✓ Paid on {formatDate(challan.paymentDate)}
                            </p>
                        </div>
                    )}
                </div>
            ))}

            {/* Payment Modal */}
            {payingChallan && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
                    <div className="bg-white p-6 rounded-lg max-w-md w-full">
                        <h3 className="text-lg font-semibold mb-4">Process Payment</h3>

                        <div className="mb-4">
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                Payment Method
                            </label>
                            <select
                                value={paymentMethod}
                                onChange={(e) => setPaymentMethod(e.target.value)}
                                className="w-full p-2 border border-gray-300 rounded-md"
                            >
                                <option value="Card">Credit/Debit Card</option>
                                <option value="Bank Transfer">Bank Transfer</option>
                                <option value="Digital Wallet">Digital Wallet</option>
                            </select>
                        </div>

                        <div className="flex space-x-4">
                            <button
                                onClick={() => handlePayment(payingChallan)}
                                className="flex-1 bg-green-600 text-white py-2 px-4 rounded-md hover:bg-green-700"
                            >
                                Pay Now
                            </button>
                            <button
                                onClick={() => setPayingChallan(null)}
                                className="flex-1 bg-gray-300 text-gray-700 py-2 px-4 rounded-md hover:bg-gray-400"
                            >
                                Cancel
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ChallanList;
